import React, { useState } from 'react';
import axios from 'axios';

function ImageUpload() {
  const [name, setName] = useState('');
  const [imageUrl, setImageUrl] = useState('');

  const handleNameChange = (event) => {
    setName(event.target.value);
  };

  const handleImageUrlChange = (event) => {
    setImageUrl(event.target.value);
  };

  const handleSubmit = async (event) => {
    event.preventDefault();
    try {
      // Send image URL instead of uploading the file
      const imageData = { name, imageUrl };
      await axios.post('http://localhost:4000/api/images', imageData);
      
      alert('Image uploaded successfully!');
      // Optionally clear form fields after successful upload
      setName('');
      setImageUrl('');
    } catch (error) {
      console.error('Error uploading image:', error);
      alert('Failed to upload image.');
    }
  };

  return (
    <div>
      <h2>Upload Image</h2>
      <form onSubmit={handleSubmit}>
        <div>
          <label htmlFor="name">Name:</label>
          <input type="text" id="name" value={name} onChange={handleNameChange} />
        </div>
        <div>
          <label htmlFor="imageUrl">Image URL:</label>
          <input type="text" id="imageUrl" value={imageUrl} onChange={handleImageUrlChange} />
        </div>
        <button type="submit">Upload</button>
      </form>
    </div>
  );
}

export default ImageUpload;
